import "core-js/modules/es.array.push.js";
import "leaflet/dist/leaflet.css";
import { LMap, LMarker, LTileLayer, LPopup } from "@vue-leaflet/vue-leaflet/src/lib";
import MarkerCluster from "./MarkerCluster.vue";
export default {
  components: {
    LMap,
    LTileLayer,
    LMarker,
    MarkerCluster,
    LPopup
  },
  data() {
    return {
      url: "https://{s}.tile.osm.org/{z}/{x}/{y}.png",
      zoom: 1,
      markers: [],
      leafletReady: false,
      leafletObject: null,
      visible: false
    };
  },
  methods: {
    async onMarkerClusterReady(markerCluster) {
      const L = await import("leaflet/dist/leaflet-src.esm");
      await this.$nextTick();
      function r(min, max) {
        return Math.random() * (max - min) + min;
      }
      let markers = [];
      for (let i = 0; i < 50000; i++) {
        const title = `marker ${i}`;
        const marker = L.marker(L.latLng(r(53.82477192, 53.92365592), r(27.5078027, 27.70640622)), {
          title: title
        });
        marker.bindPopup(title);
        markers.push(marker);
      }
      markerCluster.addLayers(markers);
    },
    async onLeafletReady() {
      this.leafletObject = this.$refs.map.leafletObject;
      this.leafletReady = true;
    }
  }
};
